tool_input.new_string:
//날짜 입력 후 미션 추가 확인
const handleConfirmAddMission = async () => {
  if (!selectedMissionId) return;

  // 날짜 유효성 검사 개선
  const today = new Date();
  today.setHours(0, 0, 0, 0); // 시간을 00:00:00으로 설정하여 날짜만 비교

  // 공개일자와 마감일자가 모두 입력된 경우
  if (releaseDate && expiryDate) {
    const releaseDateTime = new Date(releaseDate);
    const expiryDateTime = new Date(expiryDate);

    // 공개일자가 마감일자보다 늦은 경우
    if (releaseDateTime >= expiryDateTime) {
      toaster.create({
        title: "날짜 오류",
        description: "공개 일자는 마감 일자보다 이전이어야 합니다.",
        type: "error",
      });
      return;
    }
  }

  // 마감일자가 과거인 경우 경고 (허용은 하되 경고 표시)
  if (expiryDate) {
    const expiryDateTime = new Date(expiryDate);
    if (expiryDateTime < today) {
      toaster.create({
        title: "날짜 확인",
        description: "마감 일자가 오늘보다 과거입니다. 계속 진행하시겠습니까?",
        type: "warning",
      });
      // 경고는 표시하되 진행은 허용
    }
  }

  try {
    setIsLoadingMissions(true);

    // 미션 인스턴스 생성
    const newInstance: CreateJourneyMissionInstance = {
      journey_week_id: weekId,
      mission_id: selectedMissionId,
      status: "not_started" as MissionStatus,
      release_date: releaseDate || null,
      expiry_date: expiryDate || null,
      journey_id: journeyId,
    };

    await createMissionInstance(newInstance as any);

    // UI 즉시 업데이트
    await mutateMissionInstances();

    toaster.create({
      title: "미션이 추가되었습니다.",
      type: "success",
    });

    // 모달 닫기
    setShowDateModal(false);
    setShowSearch(false);
  } catch (error) {
    console.error("Error adding mission:", error);
    toaster.create({
      title: "미션 추가 실패",
      description: "미션을 추가하는 중 오류가 발생했습니다.",
      type: "error",
    });
  } finally {
    setIsLoadingMissions(false);
  }
};

