// 날짜 유효성 검사 개선 (dayjs 사용)
const today = dayjs().startOf('day');

// 공개일자와 마감일자가 모두 입력된 경우
if (releaseDate && expiryDate) {
  const releaseMoment = dayjs(releaseDate);
  const expiryMoment = dayjs(expiryDate);

  // 공개일자가 마감일자보다 늦거나 같은 경우
  if (releaseMoment.isSameOrAfter(expiryMoment)) {
    toaster.create({
      title: "날짜 오류",
      description: "공개 일자는 마감 일자보다 이전이어야 합니다.",
      type: "error",
    });
    return;
  }
}

// 마감일자가 과거인 경우 경고 (허용은 하되 경고 표시)
if (expiryDate) {
  const expiryMoment = dayjs(expiryDate);
  if (expiryMoment.isBefore(today)) {
    toaster.create({
      title: "날짜 확인",
      description: "마감 일자가 오늘보다 과거입니다.",
      type: "warning",
    });
    // 경고는 표시하되 진행은 허용
  }
}

