"use client";

import { useRef, useCallback } from "react";
import { useNotifications, markAsRead } from "@/hooks/useNotification";
import Spinner from "@/components/common/Spinner";
import NotificationCard from "@/app/(home)/_notification/NotificationCard";
import styled from "@emotion/styled";
import Text from "@/components/Text/Text";
import { getJourneyByUuidRetrieveId } from "@/utils/data/journey";
import { useEffect, useState } from "react";

interface JourneyNotificationTabProps {
  slug: string;
}

export default function JourneyNotificationTab({ slug }: JourneyNotificationTabProps) {
  const [journeyId, setJourneyId] = useState<string | null>(null);

  // Journey ID 가져오기
  useEffect(() => {
    const fetchJourneyId = async () => {
      if (slug) {
        try {
          const journeyData = await getJourneyByUuidRetrieveId(slug);
          if (journeyData && journeyData.length > 0) {
            setJourneyId(journeyData[0].id);
          }
        } catch (error) {
          console.error("Error fetching journey ID:", error);
        }
      }
    };

    fetchJourneyId();
  }, [slug]);

  const {
    notifications,
    error,
    isLoading,
    fetchNextPage,
    hasNextPage,
    isFetchingNextPage,
    mutate,
  } = useNotifications(10);

  // 해당 Journey와 관련된 알림만 필터링
  const filteredNotifications = notifications.filter(notification => {
    // journey_id가 있는 알림만 해당 Journey의 알림으로 간주
    return notification.metadata?.journey_id === journeyId;
  });

  // 무한 스크롤을 위한 인터섹션 옵저버
  const observerRef = useRef<IntersectionObserver | null>(null);
  const lastItemRef = useCallback(
    (node: HTMLDivElement | null) => {
      if (isFetchingNextPage) return;

      if (observerRef.current) {
        observerRef.current.disconnect();
      }

      observerRef.current = new IntersectionObserver((entries) => {
        if (entries[0].isIntersecting && hasNextPage) {
          fetchNextPage();
        }
      });

      if (node) {
        observerRef.current.observe(node);
      }
    },
    [isFetchingNextPage, fetchNextPage, hasNextPage]
  );

  // 알림 읽음 처리
  const handleReadNotification = async (notificationId: string) => {
    await markAsRead(notificationId);
    mutate();
  };

  if (error) {
    return <div>Error: {error.message}</div>;
  }

  if (isLoading || !journeyId) {
    return (
      <LoadingContainer>
        <Spinner size="32px" style={{ marginTop: "12px" }} />
      </LoadingContainer>
    );
  }

  return (
    <NotificationsContainer>
      {filteredNotifications.length === 0 ? (
        <EmptyState>
          <Text variant="body" color="var(--grey-600)">
            이 클래스의 알림이 없습니다.
          </Text>
        </EmptyState>
      ) : (
        <div className="notification-list">
          {filteredNotifications.map((notification, index) => {
            const isLastItem = index === filteredNotifications.length - 1;
            return (
              <div key={notification.id} ref={isLastItem ? lastItemRef : null}>
                <NotificationCard
                  notification={notification}
                  readNotification={handleReadNotification}
                />
              </div>
            );
          })}

          {isFetchingNextPage && (
            <div>
              <Spinner size="24px" style={{ marginTop: "12px" }} />
            </div>
          )}
        </div>
      )}
    </NotificationsContainer>
  );
}

const NotificationsContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 8px;
  align-items: center;
  width: 100%;
  padding: 16px;

  .notification-list {
    width: 100%;
    display: flex;
    flex-direction: column;
    gap: 8px;
  }
`;

const EmptyState = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 200px;
  width: 100%;
  background-color: var(--grey-100);
  border-radius: 8px;
  padding: 32px;
`;

const LoadingContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 200px;
  width: 100%;
`;

